		const basename = path.basename(filename);
		if (basename.startsWith('+layout.') && !content.includes('<slot')) {
			const message =
				`\n${colors.bold().red(path.relative('.', filename))}\n` +
				'`<slot />` missing — inner content will not be rendered';

			if (!warned.has(message)) {
				console.log(message);
				warned.add(message);
			}
		}
	}
