/**
 * Creates a function that can be used to report lint issues.
 */
export const createReportFunction = (_args) => {
    return (args) => {
        if (!args.node)
            return;
        args.node.lint = [
            // copy existing lint reports
            ...(args.node.lint ?? []),
            // add the new lint report to the node
            {
                id: _args.id,
                level: _args.level,
                message: args.message,
            },
        ];
    };
};
