export const createResource = (language, ...messages) => ({
    type: "Resource",
    languageTag: {
        type: "LanguageTag",
        name: language,
    },
    body: messages,
});
export const createMessage = (id, pattern) => ({
    type: "Message",
    id: { type: "Identifier", name: id },
    pattern: {
        type: "Pattern",
        elements: typeof pattern === 'string' ? [{ type: "Text", value: pattern }] : pattern,
    },
});
