import { base } from "$app/paths";
import { initRuntimeWithLanguageInformation } from "../../../runtime/index.js";
export const initSvelteKitClientRuntime = async ({ fetch, language, referenceLanguage, languages, }) => {
    const runtime = initRuntimeWithLanguageInformation({
        readResource: async (language) => fetch(`${base}/inlang/${language}.json`).then((response) => response.ok ? response.json() : undefined),
        referenceLanguage,
        languages,
    });
    if (language) {
        await runtime.loadResource(language);
        runtime.switchLanguage(language);
    }
    return runtime;
};
